import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import InputList from './component/inputList/index.js';
import ShowList from './component/showList/index.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.addTask = this.addTask.bind(this);
    this.removeTask = this.removeTask.bind(this);
    this.state = {
      taskList: ["吃饭", "睡觉", "打豆豆"]
    };
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <div className="todo-list">
          <div className="content">
            <h1>React Todo</h1>
            <ShowList  taskList={this.state.taskList} removeTask={this.removeTask} />
            <InputList taskList={this.state.taskList} addTask={this.addTask} />
          </div>
        </div>
      </div>
    );
  }

  addTask() {
    const taskList = this.state.taskList;
    const input = document.getElementById("input-task");
    taskList.push(input.value);
    input.value = '';
    this.setState({
      taskList: taskList
    })
  }

  removeTask(index) {
    console.log("删除", index);
  }
}

export default App;
